4) Touch support for the resizers in

// ...existing code...
  const startDrag = (which, e, isTouch = false) => {
    e.preventDefault?.()
    const startX = e.clientX
    const rect = gridRef.current.getBoundingClientRect()
    const total = rect.width
    const curPx = sizes.map(p => (p / 100) * total)

    const moveEvt = isTouch ? 'touchmove' : 'mousemove'
    const upEvt   = isTouch ? 'touchend'  : 'mouseup'
    const getClientX = (ev) => isTouch ? (ev.touches?.[0]?.clientX ?? startX) : ev.clientX

    const onMouseMove = (ev) => {
      const dx = getClientX(ev) - startX
      if (which === 0) {
        let newLeft = curPx[0] + dx
        let newMid = curPx[1] - dx
        if (newLeft < MIN_PX) { newMid -= (MIN_PX - newLeft); newLeft = MIN_PX }
        if (newMid < MIN_PX) { newLeft -= (MIN_PX - newMid); newMid = MIN_PX }
        const s0 = (newLeft / total) * 100
        const s1 = (newMid / total) * 100
        const s2 = 100 - s0 - s1
        setSizes([s0, s1, Math.max(s2, 0)])
      } else {
        let newMid = curPx[1] + dx
        let newRight = curPx[2] - dx
        if (newMid < MIN_PX) { newRight -= (MIN_PX - newMid); newMid = MIN_PX }
        if (newRight < MIN_PX) { newMid -= (MIN_PX - newRight); newRight = MIN_PX }
        const s1 = (newMid / total) * 100
        const s2 = (newRight / total) * 100
        const s0 = (curPx[0] / total) * 100
        setSizes([s0, s1, Math.max(s2, 0)])
      }
    }
    const onMouseUp = () => {
      window.removeEventListener(moveEvt, onMouseMove)
      window.removeEventListener(upEvt, onMouseUp)
    }
    window.addEventListener(moveEvt, onMouseMove, { passive: false })
    window.addEventListener(upEvt, onMouseUp, { passive: false })
  }
// ...existing code...
        <div
          className="resizer"
          aria-label="Resize left/middle"
          onMouseDown={(e) => startDrag(0, e, false)}
          onTouchStart={(e) => startDrag(0, e.touches[0], true)}
